import React, { useState } from 'react';

function App() {
  // Utilisation du hook useState pour gérer le state du compteur
  const [count, setCount] = useState(0);

  // Fonction pour incrémenter le compteur
  const increment = () => {
    setCount(count + 1);
  };

  // Fonction pour décrémenter le compteur, en s'assurant qu'il ne devienne pas négatif
  const decrement = () => {
    if (count > 0) {
      setCount(count - 1);
    }
  };

  // Fonction pour remettre le compteur à zéro
  const reset = () => {
    setCount(0);
  };

  return (
    <div className="App">
      <h1>Compteur</h1>
      <p>Compteur : {count}</p>
      {/* Boutons pour les interactions */}
      <button onClick={increment}>Incrémenter</button>
      <button onClick={decrement}>Décrémenter</button>
      <button onClick={reset}>Remettre à zéro</button>
    </div>
  );
}

export default App;
